sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue == '') {
		return;
	}
	var sWhat = 'u_what_needed';
	var whatNeeded = g_form.getValue(sWhat);
	//Clear all choices from what needed field choice list
	g_form.clearOptions(sWhat);

	if (newValue=='hr') {
		g_form.addOption(sWhat, 'hr1', 'Human Resources 1');
		g_form.addOption(sWhat, 'hr2', 'Human Resources 2');
		g_form.addOption(sWhat, 'other', 'Other');
	}

	if (newValue=='facilities') {
		g_form.addOption(sWhat, 'facilities1', 'Facilities 1');
		g_form.addOption(sWhat, 'facilities2', 'Facilities 2');
		g_form.addOption(sWhat, 'other', 'Other');
	}
	if(newValue=='legal') {
		g_form.addOption(sWhat, 'legal1', 'Legal 1');
		g_form.addOption(sWhat, 'legal2', 'Legal 2');
		g_form.addOption(sWhat, 'other', 'Other');
	}
	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue(sWhat, whatNeeded);
	}
}
